mapper.select:
		SELECT *
		FROM
    		(SELECT ROWNUM rn, areas.*
     		FROM areas
     		) 
		WHERE rn between #{start} and #{end}

		SELECT *
		FROM
    		(SELECT ROWNUM rn, areas.*
     		 FROM areas
     		 WHERE area = #{area}
     		 AND not sigungu in ('999')
     		) 
		WHERE rn between #{start} and #{end}
