mapper.select:
		select *
		from (
		    select rownum rn, c.*
		    from (
                    select distinct a.id, a.title, b.content, a.created_at, a.updated_at, a.big_code,
                    	   a.status                    				
                    from banner a
                    join commoncodes b
                    on a.small_code = b.small_code 
                    where b.big_code = 3
		    ) c
		)
		where rn between #{start} and #{end}

mapper.update:
update banner 
set status = 0 
where id = #{id}

mapper.select:
		    select rownum rn, c.*
		    from (
                    select distinct a.*, b.content type
                    from banner a
                    join commoncodes b
                    on a.small_code = b.small_code 
                    where b.big_code = 3
		    ) c
            where c.id = #{id}

mapper.update:
update banner
set title = #{title},
	content = #{content},
	url = #{url}
where id = #{id}
