mapper.select:
		select *
		from (
		    select rownum rn, c.*
		    from (
                select DISTINCT a.id, a.title, a.url, a.created_at, a.updated_at, a.big_code, a.small_code
                from banner a
                join commoncodes b
                on a.big_code = b.big_code
                order by id
		    ) c
		)
		where rn between #{start} and #{end}
