mapper.select:
		select *
		from (
		    select rownum rn, c.*
		    from (
                    select distinct a.id, a.title, b.content, a.created_at, a.updated_at, a.big_code
                    from banner a
                    join commoncodes b
                    on a.small_code = b.small_code 
                    where b.big_code = 3
		    ) c
		)
		where rn between #{start} and #{end}
