mapper.select:
update board 
set read_count = read_count + 1 
where id = #{boardId}

select *
from (
    select rownum rn, c.*
    from (
        select a.id, a.title, a.content, b.name, a.created_at, a.read_count, a.big_code, a.small_code
        from board a, users b
        where a.user_id = b.id
        and a.small_code = 1
        and a.is_deleted = 0		        
    ) c
)
where rn between #{start} and #{end}

select *
from (
    select rownum rn, c.*
    from (
        select a.id, a.title, a.content, b.name, a.created_at, a.read_count, a.big_code, a.small_code
        from board a, users b
        where a.user_id = b.id
        and a.small_code = 2
        and a.is_deleted = 0		        
    ) c
)
where rn between #{start} and #{end}

select *
from (
    select rownum rn, c.*
    from (
        select a.id, a.title, b.name, a.created_at, a.read_count, 
        	   a.big_code, a.small_code, a.user_id, a.comment_indent
        from board a, users b
        where a.user_id = b.id
        and a.small_code = 3
        and a.is_deleted = 0
        and a.comment_indent = 0
    ) c
)
where rn between #{start} and #{end}

select *
from (
    select rownum rn, c.*
    from (
        select a.id, a.title, b.name, a.created_at, a.read_count, a.big_code,
         	   a.small_code, a.file_name, a.file_path
        from board a, users b
        where a.user_id = b.id
        and a.small_code = 4
        and a.is_deleted = 0		        
    ) c
)
where rn between #{start} and #{end}

select *
from (
    select rownum rn, c.*
    from (
        select a.id, a.title, b.name, a.created_at, a.read_count, a.big_code, a.small_code
        from board a, users b
        where a.user_id = b.id
        and a.small_code = 5
        and a.is_deleted = 0		        
    ) c
)
where rn between #{start} and #{end}

select *
from (
    select rownum rn, c.*
    from (
        select a.id, a.user_id, a.content, b.name, b.nickname, a.created_at, a.read_count, a.big_code, a.small_code
        from board a, users b
        where a.user_id = b.id
        and a.small_code = 6
        order by a.id desc
    ) c
)
where rn between #{start} and #{end}

mapper.update:
update board 
set comment_step = comment_step + 1 
where comment_group_id = #{comment_group_id} 
and comment_step > #{comment_step}

mapper.select:
select *
from (
    select rownum rn, c.*
    from (
        select a.id, a.title, b.name, a.created_at, a.read_count, 
        	   a.big_code, a.small_code, a.user_id, a.comment_indent
        from board a, users b
        where a.user_id = b.id
        and a.small_code = 3
        and a.is_deleted = 0
        and a.comment_indent = 0
        and a.user_id = #{user_id}
    ) c
)
where rn between #{start} and #{end}

mapper.update:
update board 
set is_deleted = 1
where id = #{id}
