mapper.select:
update board 
set read_count = read_count + 1 
where id = #{boardId}

select *
from (
    select rownum rn, c.*
    from (
        select a.id, a.title, b.name, a.created_at, a.read_count, a.big_code, a.small_code
        from board a, users b
        where a.user_id = b.id
        and a.small_code = 1
        order by a.id desc
    ) c
)
where rn between #{start} and #{end}

