mapper.update:
update contents c 
set is_deleted = 1 
where c.id = #{content_id}

mapper.select:
SELECT *
FROM
(
	SELECT ROWNUM rn, a.*
	FROM
	(SELECT * FROM contents 

		WHERE big_code = #{big_code}


		AND is_deleted = #{is_deleted}


		AND small_code = #{small_code}
		) a
)
WHERE rn BETWEEN #{start} and #{end}
