mapper.select:
	SELECT *
	FROM
	(
		SELECT ROWNUM rn, a.*
		FROM
		(SELECT * 
			FROM contents c, experience e
			WHERE c.id = e.content_id
			and big_code = 15

				AND title like '%' || #{keyword} || '%'




    					AND status = #{status}  








    					AND is_deleted = #{is_deleted}  








    					AND small_code = #{small_code}  




			) a
	)
	WHERE rn BETWEEN #{start} and #{end}

mapper.update:
update contents c 
set is_deleted = 1 
where c.id = #{content_id}

update contents c 
set is_deleted = 0 
where c.id = #{content_id}

