mapper.select:
SELECT count(*)
FROM   festivals f, contents c
WHERE  f.content_id = c.id

	 AND is_deleted = #{is_deleted}


	 AND status = #{status}




	 		AND title like '%' || #{keyword} || '%'


	 		AND content like '%' || #{keyword} || '%'


	 		AND sponsor like '%' || #{keyword} || '%'


	 		AND eventplace like '%' || #{keyword} || '%'




	 AND area = #{area}

		AND sigungu = #{sigungu}





			 AND end_date < sysdate 


			 AND sysdate BETWEEN start_date AND end_date 


			 AND start_date > sysdate 




	AND small_code = #{small_code}

SELECT * FROM ( SELECT ROWNUM rn, t.*, (SELECT content FROM areas a WHERE t.area = a.area AND a.sigungu = 999) AS area_content, (SELECT content FROM areas a WHERE t.area = a.area AND t.sigungu = a.sigungu) AS sigungu_content FROM (SELECT f.*, c.*, TO_CHAR(start_date, 'YYYY-MM-DD') as startdateStr, TO_CHAR(end_date, 'YYYY-MM-DD') as enddateStr FROM festivals f, contents c WHERE f.content_id = c.id
AND is_deleted = #{is_deleted}
AND status = #{status}
AND title like '%' || #{keyword} || '%'
AND content like '%' || #{keyword} || '%'
AND sponsor like '%' || #{keyword} || '%'
AND eventplace like '%' || #{keyword} || '%'
AND area = #{area}
AND sigungu = #{sigungu}
ORDER BY c.readcount
ORDER BY content_id
AND end_date < sysdate
AND sysdate BETWEEN start_date AND end_date
AND start_date > sysdate
AND small_code = #{small_code}
) t )
WHERE rn BETWEEN #{start} and #{end}

mapper.update:
UPDATE contents
SET    readcount = readcount + 1
WHERE  id = #{contentId}

UPDATE festivals 
SET hours=#{hours}, start_date=#{start_date}, end_date=#{end_date}, sponsor=#{sponsor},
eventplace=#{eventplace}, cost=#{cost}, overview=#{overview}, is_parking=#{is_parking}, 
is_stroller=#{is_stroller}, is_wheelchair=#{is_wheelchair}, is_restroom=#{is_restroom} 
WHERE content_id = #{content_id}

mapper.delete:
DELETE ContentTags 
WHERE tag_id = #{tag_id}
AND content_id = #{content_id}
