mapper.select:
SELECT x.*, y.content AS theme FROM ( SELECT ROWNUM rn, t.*, (SELECT content FROM areas a WHERE t.area = a.area AND a.sigungu = 999) AS area_content, (SELECT content FROM areas a WHERE t.area = a.area AND t.sigungu = a.sigungu) AS sigungu_content FROM (SELECT * FROM restaurants r, contents c WHERE r.content_id = c.id
and is_deleted = #{is_deleted}
ORDER BY content_id) t ) x, commoncodes y WHERE rn BETWEEN #{start} and #{end} AND x.big_code = y.big_code AND x.small_code = y.small_code

	SELECT 	COUNT(*)
	FROM   	contents
	WHERE 	big_code = #{big_code}
	AND   	status = 1
	AND   	is_deleted = 0

		AND title like '%' || #{keyword} || '%'




    			AND small_code = #{small_code}  






		and area = #{area}

				and sigungu = #{sigungu}

	SELECT *
	FROM
	(
		SELECT ROWNUM rn, c.*
		FROM 
		(
			SELECT *
			FROM   contents
			WHERE big_code = #{big_code}
			AND   status = 1
			AND   is_deleted = 0

				AND title like '%' || #{keyword} || '%'




    					AND small_code = #{small_code}  






				and area = #{area}

				and sigungu = #{sigungu}

			) c
		)
		WHERE rn BETWEEN #{start} and #{end}

	SELECT  COUNT(*)
	FROM	
			(
				SELECT c.*
 				FROM   restaurants r, contents c
 				WHERE  r.content_id = c.id

					AND c.title like '%' || #{keyword} || '%'




			 				AND c.small_code = #{small_code} 





 					AND c.area = #{area}


 							AND c.sigungu = #{sigungu}









 							AND c.status = #{status} 








			 				AND c.is_deleted = #{is_deleted} 





 			 ) x, commoncodes y
	WHERE   x.big_code = y.big_code AND x.small_code = y.small_code

	SELECT  x.*, y.content AS theme, y.small_code, x.id AS content_id
	FROM	
			(
				SELECT ROWNUM rn, c.*
 				FROM   restaurants r, contents c
 				WHERE  r.content_id = c.id

					AND c.title like '%' || #{keyword} || '%'




			 				AND c.small_code = #{small_code} 





 					AND c.area = #{area}


 							AND c.sigungu = #{sigungu}









 							AND c.status = #{status} 








			 				AND c.is_deleted = #{is_deleted} 





 			 ) x, commoncodes y
	WHERE rn BETWEEN #{start} and #{end}
	AND   x.big_code = y.big_code AND x.small_code = y.small_code

mapper.update:
UPDATE	restaurants 
SET    	first_menu=#{first_menu}, menu=#{menu}, open_time=#{open_time}, rest_date=#{rest_date}, is_smoking=#{is_smoking}, is_packing=#{is_packing}, is_parking=#{is_parking} 
WHERE  	content_id=#{content_id}

UPDATE	contents
SET		small_code=#{small_code}, area=#{area}, sigungu=#{sigungu}, title=#{title}, address=#{address}, postcode=#{postcode}, phone=#{phone}, content=#{content}, img1=#{img1}, img2=#{img2}, img3=#{img3}  
WHERE  	id=#{content_id}

UPDATE contents 
SET    is_deleted=1
WHERE  id=#{contentId}

UPDATE contents
SET    status = #{status}
WHERE  id = #{content_id}
